var SearchURLTemplate = Class.create({
  initialize : function (params) {
    if (params) {
      if (params.subject != undefined) { 
        this.parameters.subjectparent = params.subject; 
        this.parameters.subject = params.subject;
      }
      if (params.category != undefined) { 
        this.parameters.category = params.category; 
      }
      if (params.level != undefined) { 
        this.parameters.levelparent = params.level; 
        this.parameters.level = params.level;
      }
      if (params.ict != undefined) { 
        this.parameters.ictparent = params.ict;
        this.parameters.ict = params.ict;
      }
      if (params.special != undefined) { 
        this.parameters.special = params.special; 
      }
    }
  },
  parameters : {
    subjectparent : "",
    subject : "",
    category : "",
    levelparent : "",
    level : "",
    ictparent : "",
    ict : "",
    special : ""
  },
  //urltemplate : new Template("o:s=s:resource^f=o:resource=o:subjectparent=s:#{subjectparent}^subject=s:#{subject}^category=s:#{category}^levelparent=s:#{levelparent}^level=s:#{level}^language=s:^review=s:^ictparent=s:#{ictparent}^ict=s:#{ict}^special=s:#{special}^group=o:subjectparent=s:^subject=s:^levelparent=s:^level=s:^policy=s:^language=s:^member=o:subjectparent=s:^subject=s:^member_type=s:^country=s:^blog=o:^curriki=o:^p=o:resource=o:c=n:0^s=n:25^group=o:c=n:0^s=n:25^member=o:c=n:0^s=n:25^blog=o:c=n:0^s=n:25^curriki=o:c=n:0^s=n:25^t=s:search-resource-tab^a=o:resource=o:a=b:1"),
  urltemplate : new Template("o%3As%3Ds%253Aresource%5Ef%3Do%253Aresource%253Do%25253Asubjectparent%25253Ds%2525253A#{subjectparent}%25255Esubject%25253Ds%2525253A#{subject}%25255Ecategory%25253Ds%2525253A#{category}%25255Elevelparent%25253Ds%2525253A#{levelparent}%25255Elevel%25253Ds%2525253A#{level}%25255Elanguage%25253Ds%2525253A%25255Ereview%25253Ds%2525253A%25255Eictparent%25253Ds%2525253A#{ictparent}%25255Eict%25253Ds%2525253A#{ict}%25255Especial%25253Ds%2525253A#{special}%255Egroup%253Do%25253Asubjectparent%25253Ds%2525253A%25255Esubject%25253Ds%2525253A%25255Elevelparent%25253Ds%2525253A%25255Elevel%25253Ds%2525253A%25255Epolicy%25253Ds%2525253A%25255Elanguage%25253Ds%2525253A%255Emember%253Do%25253Asubjectparent%25253Ds%2525253A%25255Esubject%25253Ds%2525253A%25255Emember_type%25253Ds%2525253A%25255Ecountry%25253Ds%2525253A%255Eblog%253Do%25253A%255Ecurriki%253Do%25253A%5Ep%3Do%253Aresource%253Do%25253Ac%25253Dn%2525253A0%25255Es%25253Dn%2525253A25%255Egroup%253Do%25253Ac%25253Dn%2525253A0%25255Es%25253Dn%2525253A25%255Emember%253Do%25253Ac%25253Dn%2525253A0%25255Es%25253Dn%2525253A25%255Eblog%253Do%25253Ac%25253Dn%2525253A0%25255Es%25253Dn%2525253A25%255Ecurriki%253Do%25253Ac%25253Dn%2525253A0%25255Es%25253Dn%2525253A25%5Et%3Ds%253Asearch-resource-tab%5Ea%3Do%253Aresource%253Do%25253Aa%25253Db%2525253A1"),
  renderTemplate : function() {
    return this.urltemplate.evaluate(this.parameters);
  },
  updateParameters : function(params) {
    this.initialize(params);
  }
});

//document.observe('xwiki:dom:loaded', function(){
Ext.onReady(function(){
  var searchUrlTemplate = new SearchURLTemplate();
  var searchForm = $('search-criteria-form');
  var radios = searchForm.getInputs('radio');
  var searchPath = "$xwiki.getURL('Search.WebHome')";
  radios.invoke('observe', 'click', function(evt){
    var radio = evt.element();
    switch(radio.name) {
      case 'criteria_grade_age_range' : 
        searchUrlTemplate.updateParameters({level : $F(radio)}); 
        break;
      case 'criteria_subject' : 
        searchUrlTemplate.updateParameters({subject : $F(radio)}); 
        break;
      case 'criteria_resource_type' : 
        searchUrlTemplate.updateParameters({category : $F(radio)}); 
        break;
      case 'criteria_usage_type' : 
        searchUrlTemplate.updateParameters({ict : $F(radio)}); 
        break;
    }
    var querystring = searchUrlTemplate.renderTemplate();
    searchForm.writeAttribute('action', searchPath + "#" + querystring);
    var link = $('submit-link');
    link.writeAttribute('href', searchForm.readAttribute('action'));
  });
});

